(window.webpackJsonp=window.webpackJsonp||[]).push([[5],{"ct+p":function(e,n,i){"use strict";i.r(n),i.d(n,"HomeModule",(function(){return m}));var t=i("ofXK"),r=i("tyNb"),s=i("fXoL"),a=i("1kSV");let u=(()=>{class e{constructor(e){this.activeModal=e}ngOnInit(){}}return e.\u0275fac=function(n){return new(n||e)(s.Lb(a.a))},e.\u0275cmp=s.Fb({type:e,selectors:[["app-card-detail"]],inputs:{card:"card"},decls:4,vars:2,consts:[["alt","Picture",1,"card-img-top",2,"border-radius","1rem",3,"src"],[1,"mt-4","p-4",2,"background-color","white","border-radius","1rem"],[2,"white-space","pre-line"]],template:function(e,n){1&e&&(s.Mb(0,"img",0),s.Qb(1,"div",1),s.Qb(2,"span",2),s.zc(3),s.Pb(),s.Pb()),2&e&&(s.ic("src",n.card.imgSrc,s.sc),s.yb(3),s.Ac(n.card.text))},styles:[""]}),e})();var c=i("R0Ic");let d=(()=>{class e{constructor(e){this.elemRef=e,this.zoomState="off",this.cardClicked=new s.n}get isZoomed(){return"on"===this.zoomState}set isZoomed(e){this.zoomState=e?"on":"off"}ngOnInit(){}get position(){return this.elemRef.nativeElement.getBoundingClientRect()}toggleZoom(){this.isZoomed=!this.isZoomed,console.log(this.zoomState)}}return e.\u0275fac=function(n){return new(n||e)(s.Lb(s.l))},e.\u0275cmp=s.Fb({type:e,selectors:[["app-card"]],inputs:{card:"card"},outputs:{cardClicked:"cardClicked"},decls:2,vars:1,consts:[[1,"card",2,"cursor","pointer",3,"click"],["alt","Picture",1,"card-img-bottom",3,"src"]],template:function(e,n){1&e&&(s.Qb(0,"div",0),s.bc("click",(function(){return n.cardClicked.emit(n.card)})),s.Mb(1,"img",1),s.Pb()),2&e&&(s.yb(1),s.ic("src",n.card.imgSrc,s.sc))},styles:[".example-card[_ngcontent-%COMP%]{max-width:400px;min-width:200px;border-width:1px;border-color:#000;margin:10px 0}.card-image[_ngcontent-%COMP%]{border-top:1px solid grey;border-bottom:1px solid grey}.front[_ngcontent-%COMP%]{z-index:1}"],data:{animation:[Object(c.j)("zoom",[Object(c.g)("on",Object(c.h)({transform:"scale(2)"})),Object(c.g)("off",Object(c.h)({transform:"scale(1)"})),Object(c.i)("off => on",[Object(c.e)("1s ease-out")]),Object(c.i)("on => off",[Object(c.e)("1s ease-in")])])]}}),e})();function h(e,n){if(1&e){const e=s.Rb();s.Ob(0),s.Qb(1,"app-card",2),s.bc("cardClicked",(function(n){return s.qc(e),s.dc().onCardClick(n)})),s.Pb(),s.Nb()}if(2&e){const e=n.$implicit;s.yb(1),s.ic("card",e)}}const l=[{path:"",component:(()=>{class e{constructor(e){this.modalService=e,this.data=[{title:"#001",text:"Jedes Grundst\xfcck hat eine andere Geometrie, ist nie gleich und immer ein Unikat, daher ist ein Denken in gleichartigen modularen Holzbausystemen zu starr und unwirtschaftlich.",imgSrc:"assets/01.png"},{title:"#002",text:"Der Entwurf im Holzbau erfordert parallele Denkwei- sen vom Groben ins Feine und umgekehrt. Es gibt schnelle Ma\xdfstabsspr\xfcnge. St\xe4dtebauliche Entschei- dungen haben weitreichende Konsequenzen bis in die Detailebene.",imgSrc:"assets/01.png"},{title:"#003",text:"In Wien wird oft in der Geb\xe4udeklasse 5 gebaut, also Geb\xe4ude mit einem Fluchtniveau von max. 22 m. Dies entspricht 8 Geschossen inkl. einem Staffelge- schoss.\n      Derzeit beschr\xe4nken aber die Brandschutzvorschrif- ten einen Holzbau mit max. 6 Geschossen.\n      Die Erf\xfcllung dieser Anforderungen ist meist keine gro\xdfe Herausforderung.\n      Will man dennoch die max. wirtschaftliche Ausnut- zung erzielen und 8-geschossig bauen, sind Sonder- l\xf6sungen mittels Brandschutzkonzept fr\xfchzeitig ein- zuplanen.",imgSrc:"assets/01.png"},{title:"#004",text:"Ein Integraler Planungsprozess ist im Holzbau von Beginn an erforderlich. Es gibt noch keine jahrzehnte lange Erfahrung im Holzbau und auch keine standardisierten Aufbauten, daher ist ein paralleler Austausch mit der Tragwerksplanung, der Bauphysik und der Technischen Geb\xe4udeausstattung notwendig.",imgSrc:"assets/01.png"},{title:"#005",text:"Ein Integraler Planungsprozess ist im Holzbau von Beginn an erforderlich. Es gibt noch keine jahrzehnte lange Erfahrung im Holzbau und auch keine standardisierten Aufbauten, daher ist ein paralleler Austausch mit der Tragwerksplanung, der Bauphysik und der Technischen Geb\xe4udeausstattung notwendig.",imgSrc:"assets/01.png"},{title:"#006",text:"Im Holzbau ist eher ein Wissensmangel auf der Planungsseite und ein Wissensvorsprung auf der Ausf\xfchrungsseite vorhanden.\n      Das \xf6ffentliche Vergaberecht verhindert allerdings Absprachen mit ausf\xfchrenden Firmen in der Planungsphase, also den Wissensaustausch,\n      daher sind andere L\xf6sungen der Kooperation erforderlich.",imgSrc:"assets/01.png"},{title:"#007",text:"Holzbauteile sind nicht standardisiert.\n      (zB. Deckenplatten usw.).\n      Daher macht es keinen Sinn Hersteller-spezifische Elementbreiten usw. im Entwurf zu ber\xfccksichtigen, da in der Vergabephase ein anderes Unternehmen den Zuschlag bekommen kann.\n      Wichtig ist nur, gewisse Bandbreiten die sich aus dem Transport und der Produktion ergeben, zu ber\xfccksichtigen.",imgSrc:"assets/01.png"},{title:"#008",text:"W\xe4nde im Holzrahmenbau werden auf sogenannten Schmetterlingwendetischen hergestellt. Diese sind meist 12m lang. Je gr\xf6\xdfer die Wandteile, desto wirt- schaftlicher ist die Produktion.",imgSrc:"assets/01.png"},{title:"#009",text:"W\xe4nde werden stehend transportiert und montiert. Decken werden liegend produziert, transportiert und montiert.",imgSrc:"assets/01.png"},{title:"#010",text:"Wirtschaftlichkeit: Jeder m3 Holz kostet!\n      Das Verh\xe4ltnis m3 zu \u20ac ist im Stahlbetonbau nicht so drastisch. Jeder Zentimeter zus\xe4tzlicher Decken- st\xe4rke kostet im Holzbau viel mehr, als im Massivbau.",imgSrc:"assets/01.png"},{title:"#011",text:"Kostenumlagerungen sind durch den Optimierungs- gedanken im Holzbau leicht m\xf6glich.\n      Einsparungen auf der einen Seite, k\xf6nnen Mehrkosten in anderen Fachgebieten bewirken.\n      Daher ist eine ganzheitliche und integrale Planung notwendig.",imgSrc:"assets/01.png"},{title:"#012",text:"Schallschutz im Holzbau ist heikler als Brandschutz! Im Massivbau geht man davon aus, dass die hohe Eigen-Masse viele Probleme l\xf6st, im Holzbau muss man vermehrt in Schichten denken und ein ganzheitliches Schallschutz-Konzept in der fr\xfchen Planungsphase ber\xfccksichtigen.",imgSrc:"assets/01.png"},{title:"#013",text:"Durch die Schichtung im Holzbau arbeitet oft die statische Lastabtragung gegen die schalltechnische Trennung. Umso wichtiger ist daher eine gleichzeitige Einbindung von Baupyhsik- und Tragwerksplanung.",imgSrc:"assets/01.png"},{title:"#014",text:"\n      Der Integrale Planungsprozess braucht mehr Budget zu Beginn, daf\xfcr sinken aber die laufenden baubegleitenden Kosten.\n      Dar\xfcber hinaus sinken die Fehleranf\xe4lligkeit in der Bauphase, die Kosten f\xfcr die Bau\xfcberwachung und teurere \xc4nderungsleistungen.",imgSrc:"assets/01.png"},{title:"#015",text:"Wer A sagt, muss auch B sagen: Holzbau ist nicht nur Ersatz des prim\xe4r Baustoffes, sondern betrifft auch Folgeentscheidungen auf \xf6kologischer und ausf\xfch- rungstechnischer Seite.\n      Keine gute Kombination sind Kunststoffd\xe4mmungen und Holzrahmenbau, Nasse Fu\xdfbodenaufbauen, Verbundwerkstoffe generell ...",imgSrc:"assets/01.png"},{title:"#016",text:"Sichtholzoberfl\xe4chen m\xfcssen fr\xfchzeitig definiert und festlegt werden.\n      Einerseits gibt es architektonische Auswirkungen, bauphysikalische Auswirkungen (keine Vorsatz- schalen), aber auch im Bauprozess ist es wichtig, auf die entsprechenden Transport-, Lagerungs-\n      und Montagebedingungen zu achten. Sichtholzoberfl\xe4chen m\xfcssen einen ganzen Bauablauf unbeschadet \xfcberstehen k\xf6nnen, daf\xfcr sind keine zeitintensiven Spachtel- und Malerarbeiten notwendig.",imgSrc:"assets/01.png"},{title:"#017",text:"Es gibt eine hohe Artenvielfalt an Holzbauweisen.\n      Je nach Geb\xe4ude gilt es das bestgeeignetste System zu finden. Grunds\xe4tzlich unterscheidet man in Rahmenbau-, Massivholzbau- und Skelettbau- weise. Diverse Eigenschaften und Parameter beein- flussen die Systemwahl: Grad der Vorfertigung, Trag- f\xe4higkeit, \xd6ffnungsanteil, Spannweiten, Bauablauf, Wertsch\xf6pfung usw.\n      Man kann die Systeme auch mischen, dadurch ergeben sich zus\xe4tzliche Wechselbeziehungen.",imgSrc:"assets/01.png"},{title:"#018",text:"Durch die vielen Entscheidungsparameter und Wechselbeziehungen entsteht eine hohe Komplexit\xe4t.\n      Der schichtenweise Aufbau erzeugt Abh\xe4ngigkeiten in der Bauphysik, Statik, Produktion und TGA, daher muss ein Bauteil integral geplant werden.\n      Weiters empfiehlt es sich daher einen Standardisie- rungsgedanken im Entwurf zu ber\xfccksichtigen, um Sonderl\xf6sungen und Neu-Erfindungen zu vermeiden, um die Komplexit\xe4t nicht noch weiter zuzuspitzen.",imgSrc:"assets/01.png"},{title:"#019",text:"Holz ist ein nat\xfcrlich gewachsener Rohstoff und hat in jede Richtung andere Festigkeiten.\n      Ein Gedankenspiel mit 100g Spaghetti \u2013 stellt man den B\xfcndel auf und belastet es gleichm\xe4\xdfig von oben, also in Faserrichtung so ist es sehr stabil.\n      Legt man den B\xfcndel aber auf den Tisch und dr\xfcckt quer zur Faser, so quetscht sich der B\xfcndel auseinander und kann nicht viel Kraft aufnehmen. Analog dazu verhalten sich die Fasern im Holz.",imgSrc:"assets/01.png"},{title:"#020",text:"Holzbauteile m\xfcssen gleichm\xe4\xdfig belastet werden, ansonsten treten zu hohe Pressungen auf. Besondere Beachtung muss dabei den Auflager- punkten gegeben werden.\n      Querpressungen sind bereits ab drei Geschossen ma\xdfgeblich zu ber\xfccksichtigen.",imgSrc:"assets/01.png"},{title:"#021",text:"Stahlbeton ist ein formbares Verbundmaterial,\n      mit dem man die Tragf\xe4higkeit durch die eingelegte Bewehrung in sp\xe4ten Bauphasen relativ kosten- g\xfcnstig steuern kann.\n      Der Holzbau hingegen hat grundlegende andere Material- und Verarbeitungseigenschaften, die man je nach Einbausituation im Vorhinein ber\xfccksichtigen muss. Daher ist ein ungeheures Ma\xdf an Genauigkeit in der Planung erforderlich.",imgSrc:"assets/01.png"},{title:"#022",text:"Holz ist ein sehr weicher Werkstoff, erst durch Verleimung k\xf6nnen plattenf\xf6rmige starre Elemente hergestellt werden.\n      Allerdings ist die Scheibensteifigkeit viel geringer als bei Stahlbeton.\n      Daher ist es sehr wichtig ein Aussteifungskonzept im Entwurf zu entwickeln.",imgSrc:"assets/01.png"},{title:"#023",text:"Wie bei einem Schraubenschl\xfcssel gilt, je gr\xf6\xdfer der Hebelsarm zur Geb\xe4udemitte, desto geringer wird die aufzubringende Kraft in der aussteifenden Wand. Daher sind hochsteife Materialien im Kern erforder- lich. Durch einen wirksamen Hebelsarm k\xf6nnen auch weichere Materialien beispielsweise in Au\xdfenw\xe4n- den verwendet werden.",imgSrc:"assets/01.png"},{title:"#024",text:"Die Statik weist die Tragf\xe4higkeit und die Gebrauchs- tauglichkeit von Bauteilen nach. Erstes weist nach, dass keine Kr\xe4fte auf ein Bauteil einwirken, die das Material nicht aush\xe4lt. Zweiteres weist nach, dass die Durchbiegung und Form\xe4nderung in Folge einer Be- lastung nicht zu gro\xdf ist. Da dieser Nachweis meist viel fr\xfcher ma\xdfgebend ist, weil Holz weicher ist und eine Form\xe4nderung daher schneller eintritt, ist die Spannweite eine sehr wirksame Stellschraube. Einige Zentimeter Reduktion k\xf6nnen ma\xdfgeblich die Durchbiegung und Schwingungserscheinungen ^reduzieren. Eine weitere wirksame Ma\xdfnahme ist die Ausbildung von durchlaufenden Bauteilen, die so- mit mehr Auflagerpunkte haben und Durchbiegungen reduzieren k\xf6nnen.",imgSrc:"assets/01.png"},{title:"#025",text:"Durch das geringe Eigengewicht von Holz sind Schub- und Zuganker zur Ableitung von horizontalen Kr\xe4ften notwendig.\n      Durch die elementierte Bauweise m\xfcssen die Kr\xe4fte pro Gescho\xdf aufgenommen und durch entsprechen- de Verbindungsmittel bis ins Erdgescho\xdf und dann in das Fundament weitergeleitet werden.\n      Die Einspannung ist im Knotenpunkt zum Fundament am h\xf6chsten und die Kr\xe4fte verringern sich nach obenhin.\n      Dementsprechend sind die Schub- und Zuganker zu dimensionieren.",imgSrc:"assets/01.png"},{title:"#026",text:"Beim Schallschutz gibt es zwei Arten von \xdcbertragung, die beachtet werden muss.\n      Erstens den Trittschall, welcher durch direkte Ber\xfch- rungspunkte (Flanken) \xfcbertragen wird. Deshalb sind konstruktive oder materielle Trennelemente n\xf6tig, um die Schall\xfcbertragung zu unterbrechen. Dies wirkt sich allerdings eher nachteilig auf die Statik aus, weshalb im- mer ein Abwegen der Ma\xdfnahmen erforderlich ist.\n      Zweitens der Luftschall, welcher ma\xdfgeblich durch Schwingungen im Medium Luft \xfcbertragen wird und durch Bauteile indirekt hindurch wandern kann. Daf\xfcr ist im Holzbau ein mehrschichtiger Aufbau mit D\xe4mp- fender Wirkung erforderlich.",imgSrc:"assets/01.png"},{title:"#027",text:"Bei Brandschutzkonzepten ist auf eine schnelle\n      und unkomplizierte Entfluchtung zu achten, sowie ausreichend Angriffsfl\xe4chen f\xfcr die Feuerwehr. Daher sind grunds\xe4tzlich kleinere freistehende Bauk\xf6rper wie Punkth\xe4user besser geeignet, als gro\xdfvolumige Zeilenbauten.",imgSrc:"assets/01.png"},{title:"#028",text:"Die Haupt-Todesursache bei Brandunf\xe4llen sind Rauchgasvergiftungen und nicht der Brand bzw. die Flamme an sich.\n      Durch die elementierte Bauweise ist es im Holzbau besonders wichtig luftdichte und hohlraum-freie Aufbauten zu konzipieren, sowie die hohe Anzahl\n      an Fugen entsprechend auszuf\xfchren.",imgSrc:"assets/01.png"},{title:"#029",text:"Holz hat ein sehr gutes W\xe4rmed\xe4mmverhalten. Daher sind schlankere Aufbauten bei gleichen W\xe4rmed\xe4mm-Anforderungen m\xf6glich. W\xe4rmebr\xfccken k\xf6nnen reduziert werden und somit sind die Oberfl\xe4chentemperaturen h\xf6her, was sich positiv auf das Raumklima auswirkt.\n      Es sinkt zB. die Gefahr der Schimmelbildung.",imgSrc:"assets/01.png"},{title:"#030",text:"Die Sommerliche \xdcberw\xe4rmung von Geb\xe4uden ist von der Bauweise prim\xe4r unabh\xe4ngig.\n      Im 24h-Rhythmus ist es tags\xfcber wichtig die W\xe4rmeeintr\xe4ge durch Beschattung zu minimieren und nachts\xfcber die W\xe4rmeabfuhr durch hohen Luftwechsel zu maximieren.\n      Dabei hat das Material der Tragkonstruktion keinen Einfluss, da innerhalb dieser 24h-Phasen die W\xe4rme- energie lediglich wenige Zentimeter (ca. 2 cm) in den oberfl\xe4chennahen Materialien eindringen kann. Somit ist es wichtig diese Schichten mit einer\n      hohen Masse auszuf\xfchren, um tags\xfcber m\xf6glichst viel W\xe4rmeenergie zu speichern, aber gleichzeitig ist es genauso wichtig diese W\xe4rmeenergie nachts\xfcber schnell wieder abgeben zu k\xf6nnen.",imgSrc:"assets/01.png"},{title:"#031",text:"Holz und Feuchtigkeit vertragen sich nicht.\n      Daher ist es immens wichtig bauphysikalische Grundprinzipien einzuhalten.\n      Raumseitig hohe Masse und luftdicht bzw.\n      6x dampfdichter als Au\xdfenseitig ausf\xfchren.\n      Das hei\xdft nach au\xdfen hin immer diffusionsoffener werden, damit m\xf6gliche anfallende Feuchtigkeit abtransportiert werden kann.",imgSrc:"assets/01.png"},{title:"#032",text:"Beim Flachdach ist auf eine m\xf6gliche Trennung von Tragebene und D\xe4mmebene zu achten, ansonsten empfiehlt es sich eine Hinterl\xfcftungsebene als Kaltdach auszubilden.\n      Nicht hinterl\xfcftete Kompaktd\xe4cher sind nur unter Einhaltung spezieller Kriterien empfehlenswert.",imgSrc:"assets/01.png"},{title:"#033",text:"F\xfcr die holzbaugerechte Technische Geb\xe4udeaus- stattung und generell bei vorgefertigten Bauweisen empfiehlt sich eine zentrale Schachtversorgung, welche dabei unabh\xe4ngig vom Tragwerk funktioniert, leicht zug\xe4nglich ist und auch erweitert werden kann. Danach ergeben sich die Anforderungen an Gr\xf6\xdfe und Lage von Installationsf\xfchrungen, welche als fixierte Parameter in die Planung einflie\xdfen.",imgSrc:"assets/01.png"}]}ngOnInit(){}onCardClick(e){this.modalService.open(u,{centered:!0,windowClass:"modal-background"}).componentInstance.card=e}}return e.\u0275fac=function(n){return new(n||e)(s.Lb(a.b))},e.\u0275cmp=s.Fb({type:e,selectors:[["app-home"]],decls:2,vars:1,consts:[[1,"row","row-cols-1","row-cols-md-3","row-cols-lg-6"],[4,"ngFor","ngForOf"],[1,"col","my-3",3,"card","cardClicked"]],template:function(e,n){1&e&&(s.Qb(0,"div",0),s.xc(1,h,2,1,"ng-container",1),s.Pb()),2&e&&(s.yb(1),s.ic("ngForOf",n.data))},directives:[t.j,d],styles:[".card-columns{margin-top:20px}.modal-background{background-color:transparent}"],encapsulation:2}),e})()}];let g=(()=>{class e{}return e.\u0275mod=s.Jb({type:e}),e.\u0275inj=s.Ib({factory:function(n){return new(n||e)},imports:[[r.c.forChild(l)],r.c]}),e})();var o=i("PCNd");let m=(()=>{class e{}return e.\u0275mod=s.Jb({type:e}),e.\u0275inj=s.Ib({factory:function(n){return new(n||e)},imports:[[t.b,g,o.a]]}),e})()}}]);